    return (
        <>
            <Pagination page={page} />
            <div className="container">
                <div className="row">
                    {page.content.map(movie => (
                            <div key={movie.id} className="col-sm-6 col-lg-4 col-xl-3 mb-3">
                                <MovieCard movie={movie} />
                            </div>
                        )
                    )}
                </div>
            </div>
        </>
    );
}

export default Listing;
